package model.v1

import org.insightedge.scala.annotation._
import scala.beans.{BeanProperty, BooleanBeanProperty}

//// Name,Altitude
case class City(

                    @BeanProperty
                    @SpaceId(autoGenerate = true)
                    var id: String,

                    @BeanProperty
                    var name: String,

                    @BooleanBeanProperty
                    var altitude: Integer

                  ) {

  def this() = this(null, null, -1)

}
