%spark

import org.apache.spark.mllib.classification.LogisticRegressionWithSGD
import org.insightedge.spark.implicits.all._
import org.apache.spark.mllib.feature._
import org.apache.spark.mllib.regression.LabeledPoint



    // Load 2 types of emails from text files: spam and ham (non-spam).
    // Each line has text from one email.
    // In this case we split the emails Seq from previous example to two groups one that we know to be spam and another with is valid
    val spam = sc.textFile("C:/train/data/spam.txt")
    val ham = sc.textFile("C:/train/data/validmail.txt")

    // Create a HashingTF instance to map email text to vectors of 100 features.
    val tf = new HashingTF(numFeatures = 100)
    // Each email is split into words, and each word is mapped to one feature.
    val spamFeatures = spam.map(email => tf.transform(email.split(" ")))
    val hamFeatures = ham.map(email => tf.transform(email.split(" ")))

    // Create LabeledPoint datasets for spam labled 1 and valid labled 0 examples.
    val positiveExamples = spamFeatures.map(features => LabeledPoint(1, features))
    val negativeExamples = hamFeatures.map(features => LabeledPoint(0, features))
    val trainingData = positiveExamples ++ negativeExamples
    trainingData.cache() // Cache data since Logistic Regression is an iterative algorithm.

    // Create a Logistic Regression learner which uses the LBFGS optimizer.
    val lrLearner = new LogisticRegressionWithSGD()
    // Run the actual learning algorithm on the training data.
    val model = lrLearner.run(trainingData)
    model.saveToGrid(sc, "emailsLRModel")
