%spark
import com.gigaspaces.FlightDelayed
import org.insightedge.spark.implicits.all._
import org.apache.spark.mllib.classification.LogisticRegressionModel

//load the model from the grid
val model = sc.loadMLInstance[LogisticRegressionModel]("DelayModel").get

//Load new data(2008) from the grid
val newdata = sc.gridSql[DelayRec]("year = ?", Seq(2008))
newdata.count

// Prepare the new data

def gen_features(rec: DelayRec): (String, Array[Double]) = {
      val values = Array(
        rec.getDepDelay().toDouble,
        rec.getMonth().toDouble,
        rec.getDayOfMonth().toDouble,
        rec.getDayOfWeek().toDouble,
        rec.getHour().toDouble,
        rec.getDistance().toDouble
      )
      new Tuple2(rec.getId(), values)
    }

 def parseData(vals: Array[Double]): LabeledPoint = {
        LabeledPoint(if (vals(0)>=15) 1.0 else 0.0, Vectors.dense(vals.drop(1)))
    }



val parsedNewData = newdata.map(delayRec => gen_features(delayRec)._2).map(parseData)
parsedNewData.cache
val scaledNewData = parsedNewData.map(x => LabeledPoint(x.label, scaler.transform(Vectors.dense(x.features.toArray))))
scaledNewData.cache

//predict
val preds_lr = scaledNewData.map(point => model.predict(point.features)) 


preds_lr.count()

//zip together data and prediction filter only delayed predictions (prediction 1.0)
val zipped = newdata.zip(preds_lr).filter(rec => rec._2 == 1.0)


//Report to the grid on flight delay predictions
val predictions = zipped.map { rec => new FlightDelayed(rec._1.getId(), true)}
predictions.saveToGrid()
